#!//bin/sh

HOST=${1:-"localhost"}

PORT=1965

check_status()
{
	local _status=$1
	local _expected=$2
	if [ "$_status" != "$_expected" ]; then
		echo "[-] failure. Expected $_expected and got $_status"
	fi
}

